(8, 'document', 'text/html', 'Дезинфекция', '', '', 'dezinfekcziya', 1, '', 1, 0, 0, 7, 1, '', '', 1, 0, 1, 1, 1, 1, 1608797794, 0, 0, 0, 0, 0, 1608797794, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'dezinfekcziya/', 0, 0, 1, NULL),
